import data from "../data/database.json";
import { data2 } from "../data/data.json";

export default function getItems() {
  return data.map((item) => {
    return {
      _id: item.id,
      title: item.title,
      image: item.image,
    };
  });
}

export const getItem = (id) => {
  return data2.find((item) => item.id === id);
};
