import React from "react";
import { Link } from "react-router-dom";

export function Title(data) {
  return (
    <div className="container__head--title">
      <h1>{data}</h1>
    </div>
  );
}

export default function Model({ data, id }) {
  return (
    <div className="container__head--model">
      {data.map((item) => {
        return (
          <Link to={`/product/${id}/${item.name}`}>
            <li className="container__head--list" key={item.index}>
              <h2>{item.name}</h2>
              <p>{item.nb_place} Places</p>
            </li>
          </Link>
        );
      })}
    </div>
  );
}

export function Modelprez() {
  return (
    <div className="container__prez">
      <div className="container__prez--image">image</div>
      <div className="container__prez--text">text</div>
    </div>
  );
}

export function Features() {
  return (
    <div className="container__feat">
      <div className="container__feat--list">liste des caract√©ristiques</div>
    </div>
  );
}
