import React, { useState } from "react";

function ContactForm() {
  const [formValues, setFormValues] = useState({
    firstName: "",
    lastName: "",
    address: "",
    postalCode: "",
    city: "",
    email: "",
    phone: "",
    message: "",
    interestedProduct: [],
  });

  const handleSubmit = (event) => {
    event.preventDefault();

    // Format the data to be sent via email
    const emailData = `Nom : ${formValues.firstName} ${
      formValues.lastName
    }\nAdresse : ${formValues.address}\nCode postal : ${
      formValues.postalCode
    }\nVille : ${formValues.city}\nE-mail : ${formValues.email}\nTéléphone : ${
      formValues.phone
    }\nProduit(s) d'intérêt : ${formValues.interestedProduct.join(
      ", "
    )}\nMessage : ${formValues.message}`;

    // Send the email using a mailto link
    window.location.href = `mailto:contact@espacedetente.fr?subject=Demande de contact&body=${encodeURIComponent(
      emailData
    )}`;

    // Clear the form fields
    setFormValues({
      firstName: "",
      lastName: "",
      address: "",
      postalCode: "",
      city: "",
      email: "",
      phone: "",
      message: "",
      interestedProduct: [],
    });
  };

  const handleChange = (event) => {
    const { name, value, type, checked } = event.target;
    const newValue =
      type === "checkbox" ? formValues.interestedProduct.concat(value) : value;
    setFormValues({ ...formValues, [name]: newValue });
  };

  return (
    <div>
      <h2>Formulaire de contact</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Nom :
          <input
            type="text"
            name="lastName"
            value={formValues.lastName}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Prénom :
          <input
            type="text"
            name="firstName"
            value={formValues.firstName}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Adresse :
          <input
            type="text"
            name="address"
            value={formValues.address}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Code postal :
          <input
            type="text"
            name="postalCode"
            value={formValues.postalCode}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Ville :
          <input
            type="text"
            name="city"
            value={formValues.city}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          E-mail :
          <input
            type="email"
            name="email"
            value={formValues.email}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Téléphone :
          <input
            type="tel"
            name="phone"
            value={formValues.phone}
            onChange={handleChange}
            required
          />
        </label>
        <label className="form_product">
          Produit(s) d'intérêt :
          <br />
          <div className="form__checkbox-main">
            <label className="form__checkbox">
              <input
                className="form__checkbox-choice"
                type="checkbox"
                name="interestedProduct"
                value="Spa Softub"
                onChange={handleChange}
              />
              Spa Softub
            </label>
            <br />
            <label className="form__checkbox">
              <input
                className="form__checkbox-choice"
                type="checkbox"
                name="interestedProduct"
                value="Spa Bois Storvatt"
                onChange={handleChange}
              />
              Spa Bois Storvatt
            </label>
            <br />
            <label className="form__checkbox">
              <input
                className="form__checkbox-choice"
                type="checkbox"
                name="interestedProduct"
                value="Bain Nordique Storvatt"
                onChange={handleChange}
              />
              Bain Nordique Storvatt
            </label>
          </div>
        </label>
        <label>
          Message :
          <textarea
            name="message"
            value={formValues.message}
            onChange={handleChange}
          />
        </label>
        <button type="submit">Envoyer</button>
      </form>
    </div>
  );
}

export default ContactForm;
