import React from "react";
import { useState } from "react";
import { Link } from "react-router-dom";

function Title({ data }) {
  return (
    <div className="container__prez--title">
      <h1>{data}</h1>
    </div>
  );
}

function Model({ data, id }) {
  return (
    <div className="container__prez--model">
      {data.map((item) => {
        return (
          <li className="container__prez--list" key={item.index}>
            <Link to={`/product/${id}/${item.name}`}>
              <h2>{item.name}</h2>
            </Link>
            <p>{item.nb_place} Places</p>
          </li>
        );
      })}
    </div>
  );
}
function Features() {
  return (
    <div className="container__feat">
      <div className="container__feat--list">liste des caract√©ristiques</div>
    </div>
  );
}

export default function Modelprez({ data, model }) {
  const [nbmodel, setmodel] = useState(0);
  console.log(nbmodel);

  console.log(model);
  return (
    <div className="container__prez">
      <Title data={data.title} />
      <Model data={model} id={data.title} />
      <div className="container__prez--model">
        <div className="container__prez--image">image</div>
        <div className="container__prez--text">{model[0].text}</div>
      </div>
      <Features />
    </div>
  );
}
