import React, { Component } from "react";
import { getAvantages } from "../libs/getItems";

class Avantage extends Component {
  render() {
    const [isHidden, setIsHidden] = useState(false);

    const handleClick = () => {
      setIsHidden(!isHidden);
    };
    const { data, manufacturer } = this.props;

    const hasManufacturer = [data].some((item) => {
      return item.fabricant === manufacturer;
    });

    const avantages = getAvantages();
    console.log({ hasManufacturer, manufacturer });
    if (hasManufacturer) {
      return (
        <div>
          <h2>Les Avantages des spas Softub</h2>
          <div className="avantage_container">
            {avantages.map((item, i) => {
              return (
                <div className="avantage-card" key={i}>
                  <img
                    src={item.image}
                    alt={item.name}
                    className="avantage-icone"
                  />
                  <h3 className="avantage-title">{item.name}</h3>
                  <p className="avantage-description">{item.description}</p>
                </div>
              );
            })}
          </div>
        </div>
      );
    } else {
      return null;
    }
  }
}

export default Avantage;
