import React, { Component } from "react";
import { getAvantages } from "../libs/getItems";

class Avantage extends Component {
  render() {
    const { data, manufacturer } = this.props;

    const hasManufacturer = data.fabricant;

    const avantageList = getAvantages();
    console.log({ hasManufacturer, manufacturer });

    if (hasManufacturer === manufacturer) {
      avantageList.map((item) => {
        return (
          <div className="avantage">
            <img src={item.image} alt={item.name} className="avantage-icone" />
            <h3 className="avantage-title">{item.name}</h3>
            <p className="avantage-description">{item.description}</p>
          </div>
        );
      });
    } else {
      return null;
    }
  }
}

export default Avantage;
