import React from "react";

function Title({ data }) {
  return (
    <div className="container__prez--title">
      <h1>{data}</h1>
    </div>
  );
}

function Model({ data, onClick }) {
  return (
    <div className="container__prez--model">
      {data.map((item) => {
        return (
          <li className="container__prez--list" key={item.index}>
            <button onClick={onClick}>
              <h2>{item.name}</h2>
            </button>
            <p>{item.nb_place} Places</p>
          </li>
        );
      })}
    </div>
  );
}
function Features() {
  return (
    <div className="container__feat">
      <div className="container__feat--list">liste des caract√©ristiques</div>
    </div>
  );
}

export default function Modelprez({ data, model }) {
  return (
    <div className="container__prez">
      <Title data={data.title} />
      <Model data={model} />
      <div className="container__prez--model">
        <div className="container__prez--image">image</div>
        <div className="container__prez--text">text</div>
      </div>
      <Features />
    </div>
  );
}
