import { getAvantages } from "../libs/getItems";

export default function Avantage({ data, fabricant }) {
  if (fabricant === "Softub") {
    return (
      <div>
        {data.map((item) => (
          <div className="avantage">
            <img src={item.image} alt={item.name} className="avantage-icone" />
            <h3 className="avantage-title">{item.name}</h3>
            <p className="avantage-description">{item.description}</p>
          </div>
        ))}
      </div>
    );
  } else {
    return;
  }
}
