import React, { useState } from "react";

function ContactForm() {
  const [formValues, setFormValues] = useState({
    firstName: "",
    lastName: "",
    address: "",
    postalCode: "",
    city: "",
    email: "",
    phone: "",
    message: "",
  });

  const handleSubmit = (event) => {
    event.preventDefault();

    // Format the data to be sent via email
    const emailData = `Nom : ${formValues.firstName} ${formValues.lastName}\nAdresse : ${formValues.address}\nCode postal : ${formValues.postalCode}\nVille : ${formValues.city}\nE-mail : ${formValues.email}\nTéléphone : ${formValues.phone}\nMessage : ${formValues.message}`;

    // Send the email using a mailto link
    window.location.href = `mailto:contact@espacedetente.fr?subject=Demande de contact&body=${encodeURIComponent(
      emailData
    )}`;

    // Clear the form fields
    setFormValues({
      firstName: "",
      lastName: "",
      address: "",
      postalCode: "",
      city: "",
      email: "",
      phone: "",
      message: "",
    });
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormValues({ ...formValues, [name]: value });
  };

  return (
    <div>
      <h2>Formulaire de contact</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Nom :
          <input
            type="text"
            name="lastName"
            value={formValues.lastName}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Prénom :
          <input
            type="text"
            name="firstName"
            value={formValues.firstName}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Adresse :
          <input
            type="text"
            name="address"
            value={formValues.address}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Code postal :
          <input
            type="text"
            name="postalCode"
            value={formValues.postalCode}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Ville :
          <input
            type="text"
            name="city"
            value={formValues.city}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          E-mail :
          <input
            type="email"
            name="email"
            value={formValues.email}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Téléphone :
          <input
            type="tel"
            name="phone"
            value={formValues.phone}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Message :
          <textarea
            name="message"
            value={formValues.message}
            onChange={handleChange}
            required
          />
        </label>
        <button type="submit">Envoyer</button>
      </form>
    </div>
  );
}

export default ContactForm;
