import { useState } from "react";


export default function ContactForm() {
    const [inputs, setInputs] = useState({});

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}))
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        alert(inputs);  
    }
    return (
        <form onSubmit={handleSubmit}>
            <label>Nom :
                <input type={"text"} name={"username"} value={inputs.username || ""} onChange={handleChange}/>
            </label>
            <label>prénom :
                <input type={"text"} name={"surname"} value={inputs.surname || ""} onChange={handleChange}/>
            </label>
            <label>Adresse :
                <input type={"text"} name={"adress"} value={inputs.adress || ""} onChange={handleChange}/>
            </label>
            <label>Code Postal :
                <input type={"number"} name={"postalCode"} value={inputs.postalCode || ""} onChange={handleChange}/>
            </label>
            <label>Ville :
                <input type={"text"} name={"city"} value={inputs.city || ""} onChange={handleChange}/>
            </label>
            <label>Téléphone :
                <input type={"number"} name={"phone"} value={inputs.phone || ""} onChange={handleChange}/>
            </label>
            <label>Description du projet :
                <input type={"text"} name={"description"} value={inputs.description || ""} onChange={handleChange}/>
            </label>
            <input type="submit" />
        </form>
    )
}
